func largestPerimeter(A []int) int {
	maxPerimeter := 0

	sort.Sort(sort.Reverse(sort.IntSlice(A)))

	for i, a := range A {
		for j, b := range A {
			if i == j {
				continue
			}
			for k, c := range A {
				if j == k || i == k {
					continue
				}
				if !isTriangle(a, b, c) {
					continue
				}
				maxPerimeter = MaxInt(maxPerimeter, a+b+c)
			}
		}
	}

	return maxPerimeter
}

func isTriangle(a, b, c int) bool {
	if a+b <= c {
		return false
	}
	if a+c <= b {
		return false
	}
	if b+c <= a {
		return false
	}
	return true
}

func MaxInt(a, b int) int {
	if a > b {
		return a
	} else {
		return b
	}
}
